(function ($, Drupal) {
  /****************************** Behaviors ***********************************/

  Drupal.behaviors.basicTheme = {
    attach: function (context, settings) {
      // Attach all theme specific behaviors
      Drupal.basicTheme.attachBehaviors(context, settings);
    }
  }

  Drupal.basicTheme = Drupal.basicTheme || {};
  Drupal.basicTheme.behaviors = Drupal.basicTheme.behaviors || {};

  Drupal.basicTheme.attachBehaviors = function (context, settings) {
    $.each(this.behaviors, function () {
      this(context, settings);
    });
  }
  /**
   * Behaviors specific to the login forms
   */
  Drupal.basicTheme.behaviors.loginForms = function(context, settings) {
    // Sign in block labels
    $(".form-item label", context).inFieldLabels();
  }

  $(document).ready(function(){
    //active menu
    var height = screen.height/2;
    $(".various").fancybox({
      helpers: {
        // Enable the media helper to better handle video.
        media: {
         youtube : {
             params : {
              autohide : 0
            }
          }
        }
      },
      width : 16/9.6 * height,
      height : height,
      fitToView	: false,
      closeClick	: false,
      maxWidth: '95%',
      openEffect	: 'none',
      closeEffect	: 'none',
      padding: 0
    });

    $('.js-menu-trigger').on('click touchstart', function(e){
      $('.js-menu').toggleClass('is-visible');
      $('.js-menu-screen').toggleClass('is-visible');
      e.preventDefault();
    });

    //Opens the sign in block
  if (Modernizr.mq('only screen and (min-width: 900px)')) {
    $(".menu-486 a, a#login-link").click(function () {
        if($(this).hasClass('active-box')) {
            hideBox('#user-login-form',$(this));
        } else {
            showBox('#user-login-form',$(this));
            $('#user-login-form input').eq(0).select();
        }
        return false;
    });
  }

  if (Modernizr.mq('only screen and (max-width: 900px)')) {
    
  }

    $(document).click(function () {
      if($('.menu-486 a, a#login-link').hasClass('active-box')) {
        hideBox('#user-login-form',$(".menu-486 a, a#login-link"));
      }
    });

    $('#user-login-form').click(function(event){
      event.stopPropagation();
    });

    function showBox(el,link) {
      $(el).fadeIn(200,'easeOutQuad');
      $(link).addClass('active-box');
    }
    function hideBox(el, link) {
      $(link).removeClass('active-box');
      $(el).fadeOut(400,'easeOutQuad');
    }

    //sets equal height for responsive
    equalheight = function(container){

    var currentTallest = 0,
         currentRowStart = 0,
         rowDivs = new Array(),
         $el,
         topPosition = 0;
     $(container).each(function() {

       $el = $(this);
       $($el).height('auto')
       topPostion = $el.position().top;

       if (currentRowStart != topPostion) {
         for (currentDiv = 0 ; currentDiv < rowDivs.length ; currentDiv++) {
           rowDivs[currentDiv].height(currentTallest);
         }
         rowDivs.length = 0; // empty the array
         currentRowStart = topPostion;
         currentTallest = $el.height();
         rowDivs.push($el);
       } else {
         rowDivs.push($el);
         currentTallest = (currentTallest < $el.height()) ? ($el.height()) : (currentTallest);
      }
       for (currentDiv = 0 ; currentDiv < rowDivs.length ; currentDiv++) {
         rowDivs[currentDiv].height(currentTallest);
       }
     });
    }

    $(window).load(function() {
      equalheight('.quiz-box');
    });


    $(window).resize(function(){
      equalheight('.quiz-box');
    });
      
  });

})(jQuery, Drupal);
